mapper.select:
SELECT id, email, password, nickname, tel, birthday
FROM users
WHERE id = #{id}

SELECT id, email, password, nickname, tel, birthday
FROM users
WHERE email = #{email}

mapper.update:
UPDATE users
email=#{user.email}, password = #{user.password}, nickname = #{user.nickname}, tel = #{user.tel}, birthday = #{user.birthday}
WHERE id = #{user.id}

mapper.select:
SELECT EXISTS(
               SELECT 1 FROM users WHERE email = #{email}
           )
