mapper.update:
UPDATE members
passwd=#{password}, nickname=#{nickname}, phone=#{telephone},
where member_id=#{id}

mapper.select:
SELECT m.member_id, m.email, m.nickname
FROM members m
WHERE m.email = #{email}
  AND m.passwd = #{password}

SELECT m.member_id, m.email, m.nickname
FROM members m
WHERE m.member_id = #{id}

SELECT m.member_id, m.email, m.nickname
FROM members m
WHERE m.member_id = #{id}
  AND m.passwd = #{password}
