mapper.update:
UPDATE office
office_name=#{name}, address=#{address},
WHERE office_id=#{id}

mapper.select:
SELECT o.office_id, o.office_name, o.address, ST_AsText(location) AS location
FROM office o
WHERE o.office_id = #{id}

SELECT o.office_id, o.office_name, o.address
FROM office o
WHERE o.office_name = #{officeName}
