mapper.update:
UPDATE office
office_name=#{name}, address=#{address}, latitude=#{latitude}, longitude=#{longitude},
WHERE office_id=#{id}

mapper.select:
SELECT o.office_id, o.office_name, o.address, o.latitude, o.longitude
FROM office o
WHERE o.office_id = #{id}

SELECT o.office_id, o.office_name, o.address
FROM office o
WHERE o.office_name = #{officeName}
